Method.Implementation:
set tSC = $$$OK
try {
	set tSC = ..GetSourcesInternal(pDomainId)
	quit:$$$ISERR(tSC)
} catch (ex) {
	set tSC = ex.AsStatus()
}
quit ..ErrorHandler(tSC)

set tSC = $$$OK try { set:$data(%response) %response.ContentType="application/json"
set tFilter = "" if $g(%request.Data("SourceFilter",1)) { set tFilter = ..GetNotInSetFilter(pDomainId, .tSC) quit:$$$ISERR(tSC) }
set tEntityId = ##class(%iKnow.Queries.EntityAPI).GetId(pDomainId, pEntity) set tHighlight("ENTITY", tEntityId) = "<b class=""entity"" title=""entity '"_$zcvt(pEntity,"O","HTML")_"'"">"
set tSC = ##class(%iKnow.Queries.SentenceAPI).GetByEntities(.tSentences, pDomainId, pEntity, tFilter, 1, ..#IKPAGESIZE) quit:$$$ISERR(tSC)
set tJSON = ##class(%ZEN.proxyObject).%New() set tJSON.Sentences = ##class(%ListOfObjects).%New() set i = "" for { set i = $order(tSentences(i), 1, tSentData) quit:i="" set tSentId = $lg(tSentData,3) set tProxy = ##class(%ZEN.proxyObject).%New() set tProxy.SourceId = $lg(tSentData,1) set tProxy.ExternalId = $lg(tSentData,2) set tProxy.SentenceId = $lg(tSentData,3) set tProxy.Text = ..GetHighlightedSentence(pDomainId, tProxy.SentenceId, .tHighlight, .tSC) quit:$$$ISERR(tSC)
if pIncludeSentimentFlags { set tSC = ##class(%iKnow.Queries.SentenceAPI).GetParts(.tParts, pDomainId, tProxy.SentenceId) quit:$$$ISERR(tSC) set j = "" for { set j = $order(tParts(j),1,tPartData) quit:j="" continue:$lg(tPartData,2)'=tEntityId set:##class(%iKnow.Queries.EntityAPI).IsAttributed(pDomainId, $lg(tPartData,1), $$$IKATTSENPOSITIVE) tProxy.HasPositive = 1 set:##class(%iKnow.Queries.EntityAPI).IsAttributed(pDomainId, $lg(tPartData,1), $$$IKATTSENNEGATIVE) tProxy.HasNegative = 1 } }
do tJSON.Sentences.Insert(tProxy) } quit:$$$ISERR(tSC)
do tJSON.%ToJSON(,..#JSONFORMATPARAMS)
} catch (ex) { set tSC=ex.AsStatus() } quit ..ErrorHandler(tSC)

