Method.Description:
Retrieves information about all sets stored for domain <var>pDomain</var>
Writes the following JSON output:
<blockquote><pre>{
	Sets: [ 
				{
					SetId: 123,
					Name: 'abc',
					Entities: [ 'xyz', 'def', ... ]
				},... ],
	TotalInSets: x,
	TotalNotInSets: x,
	TotalSources: x
}</pre></blockquote>

Method.Implementation:
set tSC = $$$OK try { set:$data(%response) %response.ContentType="application/json" set tJSON = ##class(%ZEN.proxyObject).%New()
set tSQL = "SELECT ID FROM Demo_SetAnalysis.SetDefinition WHERE DomainId = "_pDomainId set tResult = ##class(%SQL.Statement).%ExecDirect(,tSQL) set tJSON.Sets = ##class(%ListOfObjects).%New() while tResult.%Next() { set tSetDef = ##class(SetDefinition).%OpenId(tResult.%Get("ID")) set tProxy = ##class(%ZEN.proxyObject).%New() set tProxy.SetId = tResult.%Get("ID") set tProxy.Name = tSetDef.Name set tProxy.Entities = tSetDef.Entities set tProxy.Sources = tSetDef.GetFilter().FilteredSourceCount do tJSON.Sets.Insert(tProxy) }
// now get the counts set tJSON.TotalSources = ##class(%iKnow.Queries.SourceAPI).GetCountByDomain(pDomainId) set tJSON.TotalNotInSets = ..GetNotInSetFilter(pDomainId, .tSC).FilteredSourceCount quit:$$$ISERR(tSC) set tJSON.TotalInSets = tJSON.TotalSources - tJSON.TotalNotInSets
// write JSON do tJSON.%ToJSON(,..#JSONFORMATPARAMS) } catch (ex) { set tSC = ex.AsStatus() } s ^bdb($i(^bdb)) = tSC quit ..ErrorHandler(tSC)

set tSC = $$$OK
try {
	set tJSONString = ""
	while '%request.Content.AtEnd {
		set tJSONString = tJSONString_$zcvt(%request.Content.ReadLine(),"I","UTF8")
	}
	set tSC = ##class(%ZEN.Auxiliary.jsonProvider).%ConvertJSONToObject(tJSONString,,.tJSON)
	quit:$$$ISERR(tSC)

	// retrieve or instantiate set
	set:tJSON.SetId tSetDef = ##class(SetDefinition).%OpenId(tJSON.SetId)
	set:$g(tSetDef)="" tSetDef = ##class(SetDefinition).NameIdxOpen(pDomainId, tJSON.Name)
	if tSetDef="" {
		set tSetDef = ##class(SetDefinition).%New()
		set tSetDef.Name = tJSON.Name
		set tSetDef.DomainId = pDomainId
	}

	// check which entities are already in there
	for i = 1:1:tSetDef.Entities.Count() {
		set tEntity = tSetDef.Entities.GetAt(i)
		set tCurrentEntities(tEntity)=""
	}

	// append entities
	for i = 1:1:tJSON.Entities.Count() {
		set tEntity = tJSON.Entities.GetAt(i)
		do:'$d(tCurrentEntities(tEntity)) tSetDef.Entities.Insert(tEntity)
	}

	set tSC = tSetDef.%Save()
	quit:$$$ISERR(tSC)

} catch (ex) {
	set tSC = ex.AsStatus()
}
quit ..ErrorHandler(tSC)

set tSC = $$$OK
try {
	set tJSONString = ""
	while '%request.Content.AtEnd {
		set tJSONString = tJSONString_$zcvt(%request.Content.ReadLine(),"I","UTF8")
	}
	set tSC = ##class(%ZEN.Auxiliary.jsonProvider).%ConvertJSONToObject(tJSONString,,.tJSON)
	quit:$$$ISERR(tSC)

	// retrieve set
	set:tJSON.SetId tSetDef = ##class(SetDefinition).%OpenId(tJSON.SetId)
	set:$g(tSetDef)="" tSetDef = ##class(SetDefinition).NameIdxOpen(pDomainId, tJSON.Name)
	quit:tSetDef=""

	// check which entities are already in there
	for i = 1:1:tSetDef.Entities.Count() {
		if (tJSON.RemoveEntity=tSetDef.Entities.GetAt(i)) {
			do tSetDef.Entities.RemoveAt(i)
			quit
		}
	}

	set tSC = tSetDef.%Save()
	quit:$$$ISERR(tSC)

} catch (ex) {
	set tSC = ex.AsStatus()
}
quit ..ErrorHandler(tSC)

Method.Description:
Deletes a <class>SetDefinition</class> instance from the table.
Accepts the following JSON (as HTTP POST content):
<blockquote><pre>{
		SetId: 123
}</pre></blockquote>

Method.Implementation:
set tSC = $$$OK
try {
	set tJSONString = ""
	while '%request.Content.AtEnd {
		set tJSONString = tJSONString_$zcvt(%request.Content.ReadLine(),"I","UTF8")
	}
	set tSC = ##class(%ZEN.Auxiliary.jsonProvider).%ConvertJSONToObject(tJSONString,,.tJSON)
	quit:$$$ISERR(tSC)

	set tSC = ##class(SetDefinition).%DeleteId(tJSON.SetId)
	quit:$$$ISERR(tSC)
} catch (ex) {
	set tSC = ex.AsStatus()
}
quit ..ErrorHandler(tSC)

Method.Description:
Retrieves the overlap information for <class>SetDefintion</class> <var>pSetId</var>, listing
how many sources it has in common with all other sets.
Writes the following JSON:
<blockquote><pre>{
	SetId: 123,
	Name: 'abc',
	Overlaps: [
				{
					SetId: 456,
					Name: 'def',
					Count: 789
				},... ]
}</pre></blockquote>

Method.Implementation:
set tSC = $$$OK try { set:$data(%response) %response.ContentType="application/json"
set tSetDef = ##class(SetDefinition).%OpenId(pSetId) set tJSON = ##class(%ZEN.proxyObject).%New() set tJSON.SetId = pSetId set tJSON.Name = tSetDef.Name
set tSQL = "SELECT ID FROM Demo_SetAnalysis.SetDefinition WHERE DomainId = "_pDomainId //_" AND ID != "_pSetId set tResult = ##class(%SQL.Statement).%ExecDirect(,tSQL) set tJSON.Overlaps = ##class(%ListOfObjects).%New() while tResult.%Next() { set tOtherSetDef = ##class(SetDefinition).%OpenId(tResult.%Get("ID")) set tProxy = ##class(%ZEN.proxyObject).%New() set tProxy.SetId = tResult.%Get("ID") set tProxy.Name = tOtherSetDef.Name
set tFilter = ##class(%iKnow.Filters.GroupFilter).%New(pDomainId,$$$GROUPFILTERAND) do tFilter.AddSubFilter(tSetDef.GetFilter()) do tFilter.AddSubFilter(tOtherSetDef.GetFilter()) set tProxy.Count = tFilter.FilteredSourceCount
do tJSON.Overlaps.Insert(tProxy) }
// write JSON do tJSON.%ToJSON(, ..#JSONFORMATPARAMS) } catch (ex) { set tSC = ex.AsStatus() } quit ..ErrorHandler(tSC)

set pSC = $$$OK, tFilter=""
try {
	set tAllEntities=""
	set tResult = ##class(%SQL.Statement).%ExecDirect(,"SELECT $listtostring(Entities) FROM Demo_SetAnalysis.SetDefinition WHERE DomainId = "_pDomainId)
	while tResult.%Next() {
		set tEntities = tResult.%GetData(1), tCount = $l(tEntities,",")
		for i = 1:1:tCount {
			set tEntity = $p(tEntities,",",i)
			continue:tEntity=""
			set tAllEntities = tAllEntities _ $lb(tEntity)
		}
	}
	if '$d(tAllEntities) {
		set tFilter = ##class(%iKnow.Filters.SourceIdFilter).%New(pDomainId, 0)
	} else {
		set tFilter = ##class(%iKnow.Filters.GroupFilter).%New(pDomainId,$$$GROUPFILTERAND,1) // =NOT
		do tFilter.AddSubFilter(##class(%iKnow.Filters.ContainsEntityFilter).%New(pDomainId, .tAllEntities))
	}
} catch (ex) {
	set pSC = ex.AsStatus()
}
quit tFilter

set tSC = $$$OK try { set:$data(%response) %response.ContentType="application/json"
set tJSON = ##class(%ZEN.proxyObject).%New()
set tDomain = ##class(%iKnow.Domain).%OpenId(pDomainId) if '$isobject(tDomain) { set tSC = $$$ERROR($$$IKNoDomainWithId, pDomainId) } set tPackageName = tDomain.GetParameter($$$IKPPACKAGE) if (tPackageName="") { write "{ ""Entities"": [] }" quit } set tPartTable = $tr(tPackageName,".","_")_".Part"
set tSQL = "SELECT * FROM "_ "(SELECT Entity->EntityValue EntityValue, SUM(Positive) TotalPositive, SUM(Negative) TotalNegative, Entity->CorpusFrequency TotalFrequency FROM "_ "((SELECT Entity, 1 Positive, 0 Negative FROM "_tPartTable_" WHERE FOR SOME %ELEMENT(Attributes) (%KEY = 'ATP:5') AND EntTypeId = 0) "_ "UNION ALL "_ "(SELECT Entity, 0 Positive, 1 Negative FROM "_tPartTable_" WHERE FOR SOME %ELEMENT(Attributes) (%KEY = 'ATP:6') AND EntTypeId = 0)) u "_ "GROUP BY Entity) "_ "ORDER BY (TotalPositive+TotalNegative) DESC"
set tResult = ##class(%SQL.Statement).%ExecDirect(,tSQL) set tJSON.Entities = ##class(%ListOfObjects).%New() while tResult.%Next() { set tEntity = ##class(%ZEN.proxyObject).%New() set tEntity.EntityValue = tResult.%Get("EntityValue") set tEntity.TotalPositive = tResult.%Get("TotalPositive") set tEntity.TotalNegative = tResult.%Get("TotalNegative") set tEntity.TotalFrequency = tResult.%Get("TotalFrequency") do tJSON.Entities.Insert(tEntity) }
// write JSON do tJSON.%ToJSON(, ..#JSONFORMATPARAMS) } catch (ex) { set tSC = ex.AsStatus() } quit ..ErrorHandler(tSC)

set tSC = $$$OK
try {
	set:$data(%response) %response.ContentType="application/json"

	set tJSON = ##class(%ZEN.proxyObject).%New()

	set tDomain = ##class(%iKnow.Domain).%OpenId(pDomainId)
	if '$isobject(tDomain) { set tSC = $$$ERROR($$$IKNoDomainWithId, pDomainId) }
	set tPackageName = tDomain.GetParameter($$$IKPPACKAGE)
	set tJSON.HasTables = (tPackageName'="")

	if tJSON.HasTables {
		set tSQL = "SELECT TOP 1 %ID FROM "_$tr(tPackageName,".","_")_".PathAttribute UNION SELECT TOP 1 %ID FROM "_$tr(tPackageName,".","_")_".SentenceAttribute"
		set tResult = ##class(%SQL.Statement).%ExecDirect(,tSQL)
		set tJSON.HasSentiment = (tResult.%Next() && tResult.%GetData(1))
	} else {
		set tJSON.HasSentiment = 0
	}

	// write JSON
	do tJSON.%ToJSON(, ..#JSONFORMATPARAMS)
} catch (ex) {
	set tSC = ex.AsStatus()
}
quit ..ErrorHandler(tSC)
